mapper.insert:
insert into todo_info (id, title, creator, 
  create_time, update_time, content
  )
values (#{id,jdbcType=BIGINT}, #{title,jdbcType=VARCHAR}, #{creator,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.update:
update todo_info
set title = #{title,jdbcType=VARCHAR},
  creator = #{creator,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select from todo_info where 1=1
and title like concat('%', concat(#{title}, '%'))
and update_time > {#time}
order by update_time desc limit #{pageSize}
